import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { ClothingService } from '.././clothing.service'; // Mettez à jour ce chemin selon la structure de votre projet

@Component({
  selector: 'app-clothing-form',
  templateUrl: './clothing-form.component.html',
  styleUrls: ['./clothing-form.component.css']
})
export class ClothingFormComponent implements OnInit {
  clothingForm: FormGroup;

  constructor(
    private fb: FormBuilder,
    private clothingService: ClothingService
  ) {
    this.clothingForm = this.fb.group({
      name: ['', Validators.required],
      description: [''],
      image_url: [''],
      category_id: ['', Validators.required],
      user_id: ['', Validators.required] // Assurez-vous d'avoir un moyen d'obtenir l'ID de l'utilisateur actuel
    });
  }

  ngOnInit(): void {
  }

  onSubmit(): void {
    if (this.clothingForm.valid) {
      this.clothingService.addClothingItem(
        this.clothingForm.value.name,
        this.clothingForm.value.description,
        this.clothingForm.value.image_url,
        this.clothingForm.value.category_id,
        this.clothingForm.value.user_id
      ).subscribe({
        next: (response) => {
          console.log('Vêtement ajouté avec succès', response);  
          this.clothingForm.reset();
        },
        error: (error) => {
          console.error('Erreur lors de l\'ajout du vêtement', error);
          // Gérez ici les erreurs, par exemple en affichant un message d'erreur à l'utilisateur
        }
      });
    }
  }
}
