// clothing.service.ts

import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface ClothingItem {
  id: number;
  name: string;
  description: string;
  image_url: string;
  category: string;
}

@Injectable({
  providedIn: 'root'
})
export class ClothingService {
  private baseUrl = 'http://localhost:8000';  // URL de votre API FastAPI

  constructor(private http: HttpClient) {}

  // Dans votre ClothingService Angular

getClothingItems(): Observable<ClothingItem[]> {
  const token = localStorage.getItem('auth_token');  // Récupérez le jeton depuis le stockage local
  const headers = new HttpHeaders({
    'Authorization': `Bearer ${token}`
  });

  return this.http.get<ClothingItem[]>(`${this.baseUrl}/clothing-items/`, { headers });  // Passez les en-têtes à la requête HTTP
}

}
