import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http'; // Importer HttpHeaders
import { Observable } from 'rxjs';
import { tap, catchError } from 'rxjs/operators';
import { Router } from '@angular/router';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  private baseUrl = 'http://localhost:8000'; // URL de votre API

  constructor(private http: HttpClient) {}
  createUser(user: any): Observable<any> {
    return this.http.post(`${this.baseUrl}/users/`, user);
  }


  login(username: string, password: string): Observable<{ access_token: string }> {
    // Préparation des données de formulaire URL-encodées
    const body = new URLSearchParams();
    body.set('username', username);
    body.set('password', password);

    console.log('Tentative de connexion avec :', { username, password });

    return this.http.post<{ access_token: string }>(`${this.baseUrl}/token`, body.toString(), {
      headers: new HttpHeaders({ 'Content-Type': 'application/x-www-form-urlencoded' }) // Utilisation du Content-Type pour les données de formulaire
    }).pipe(
      tap(res => {
        console.log('Réponse de connexion :', res);
        localStorage.setItem('auth_token', res.access_token);
        const userData = { username: username, email: 'email@example.com' }; // Remplacez par les vraies données de l'utilisateur
        localStorage.setItem('user', JSON.stringify(userData));
      })
    );
  }

  logout(): void {
    localStorage.removeItem('auth_token');
    localStorage.removeItem('user'); // Assurez-vous de supprimer également les informations de l'utilisateur
    this.router.navigate(['/login']); // Redirigez l'utilisateur vers la page de connexion
  }
}
