// clothing.service.ts

import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

export interface ClothingItem {
  id: number;
  name: string;
  description: string;
  image_url: string;
  category: string;
}

@Injectable({
  providedIn: 'root'
})
export class ClothingService {
  private baseUrl = 'http://localhost:8000';  // URL de votre API FastAPI

  constructor(private http: HttpClient) {}

  getClothingItems(): Observable<ClothingItem[]> {
    return this.http.get<ClothingItem[]>(`${this.baseUrl}/clothing-items/`); // Assurez-vous que le chemin correspond à votre endpoint FastAPI
  }
}
