import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { tap } from 'rxjs/operators';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  constructor(private http: HttpClient) { }

  private baseUrl = 'http://localhost:8000';

  createUser(user: any): Observable<any> {
    return this.http.post(`${this.baseUrl}/users/`, user);
  }


  login(username: string, password: string): Observable<{ access_token: string }> {
    console.log('Tentative de connexion avec :', { username, password }); // Ajout d'un console.log pour voir les données

    return this.http.post<{ access_token: string }>(`${this.baseUrl}/token`, { username, password }, {
      headers: new HttpHeaders({ 'Content-Type': 'application/json' })
    }).pipe(
      tap(res => {
        console.log('Réponse de connexion :', res); // Voir la réponse
        localStorage.setItem('auth_token', res.access_token);
      })
    );
  }


}
